data = {
  const x = d3.range(-10, 10, 0.01);
  const sins = x.map(x =&gt; ({x: x, y: Math.sin(- x * mu), type: "sin(w .)"}));
  const coss = x.map(x =&gt; ({x: x, y: Math.cos(- x * mu), type: "cos(w .)"}));
  return sins.concat(coss)

}

mu = inputs[0]
